package com.huhuhu.directive;import java.util.List;import org.apache.commons.collections.CollectionUtils;import freemarker.template.TemplateMethodModelEx;import freemarker.template.TemplateModelException;/** * ${indexOf("ab","b")} */public class IndexOfMethod implements TemplateMethodModelEx {    @Override    public Object exec(List arguments) throws TemplateModelException {        if (CollectionUtils.isEmpty(arguments)) {            return -1;        }        if (arguments.size() > 2) {            return -1;        }        String source = String.valueOf(arguments.get(0));        String target = String.valueOf(arguments.get(1));        int index = source.indexOf(target);        return index == -1 ? index : index + 1;    }}